definition.script:
def name = "test-s3-big-file"
def file = "test.bin"

timestamps {
    node() {
      try{
        stage('Generating ${file}') {
          sh "[ -f ${file} ] || dd if=/dev/urandom of=${file} bs=10240 count=102400"
        }
        stage('Archive') {
          archiveArtifacts file
        }
        stage('Unarchive') {
          unarchive mapping: ["${file}": 'test.bin']
        }      
      } finally {
        deleteDir()
      }
    }
}
